<?phpfunction filkor_getFileNameFromMysql($fileId){	$sql = mysql_query("SELECT original_filename FROM files WHERE fileid = '".$fileId."'");	$rowExists = is_resource($sql) && (mysql_num_rows($sql) > 0);		if (!$rowExists) {		error_log('Row does not exists', 0);		header('Location: /404/');		exit;	}		$row = mysql_fetch_assoc($sql);	return $row['original_filename'];}/** * Returns the row from the files table based on the fileid * @retrun array() * */function filkor_getFileRowFromMysql($fileId){	$sql = mysql_query("SELECT * FROM files WHERE fileid = '".$fileId."'");	$rowExists = is_resource($sql) && (mysql_num_rows($sql) > 0);		if (!$rowExists) {		error_log('Row does not exists', 0);		header('Location: /404/?=c');		exit;	}		$row = mysql_fetch_assoc($sql);	return $row;}?>